
/*
    Length of Longest Common Subsequence in two strings [Medium] [Recursion, Memoization, Tabulation]
*/

#include <bits/stdc++.h>
using namespace std;

class Memoization
{
private:
    int lcs(string s, string t, int i, int j, vector<vector<int>> &dp)
    {
        if (i < 0 || j < 0)
            return 0;
        if (dp[i][j] != -1)
            return dp[i][j];
        if (s[i] == t[j])
            return dp[i][j] = 1 + lcs(s, t, i - 1, j - 1, dp);
        return dp[i][j] = max(lcs(s, t, i, j - 1, dp), lcs(s, t, i - 1, j, dp));
    }

public:
    int longestCommonSubsequence(string text1, string text2)
    {
        int n = text1.size(), m = text2.size();
        vector<vector<int>> dp(n, vector<int>(m, -1));
        return lcs(text1, text2, n - 1, m - 1, dp);
    }
};

class Tabulation {
public:
    int longestCommonSubsequence(string text1, string text2) {
        int n = text1.size(), m = text2.size();
        vector<int> prev(m+1, 0), curr(m+1, 0);
        for(int i= 1; i<=n; i++){
            for(int j = 1; j<=m; j++){
                if(text1[i-1] == text2[j-1])
                    curr[j] = 1 + prev[j-1];
                else
                    curr[j] = max(prev[j], curr[j-1]);
            }
            prev = curr;
        }
        return prev[m];
    }
};

int main()
{
    string text1 = "";
    string text2 = "";

    string text3 = "abcde", text4 = "ace"; 
    Memoization obj;
    cout << obj.longestCommonSubsequence(text3, text4) << endl;
    Tabulation obj1;
    cout << obj1.longestCommonSubsequence(text1, text2) << endl;
    return 0;
}